<script>
import LogoSmall from '~/assets/images/logo/logo-small.svg'
export default {
  components: {
    LogoSmall
  },
  methods: {
    rightClick() {
      this.$router.push({path: '/logo'})
      return false
    },

    animate() {
      const logo = document.querySelector('.animated-logo .logo');
      const logoSvg = document.querySelector('.animated-logo .logo svg');
      logo.style.width = '0px';
      logoSvg.style.transform = 'translateX(0)';

      this.$anime.timeline().add({
        targets: '.animated-logo .all',
        translateX: {
          value: [0, -30],
          duration: 1150,
          easing: 'easeInOutQuad'
        }
      });

      this.$anime.timeline().add({
        targets: '.animated-logo .second',
        translateX: {
          value: [-35, 10],
          duration: 1000,
          easing: 'easeInQuad'
        },
        scale: {
          value: [1, 0],
          duration: 200,
          delay: 800,
          easing: 'easeInQuad'
        }
      });

      this.$anime.timeline().add({
        targets: '.animated-logo .logo',
        width: {
          value: [0, 100],
          duration: 400,
          delay: 750,
          easing: 'easeInOutQuad'
        },
        translateX: {
          value: [0, -16],
          duration: 400,
          delay: 1350,
          easing: 'easeInOutQuint'
        }
      });

      this.$anime.timeline().add({
        targets: '.animated-logo .ball',
        translateX: {
          value: [0, -16],
          duration: 400,
          delay: 1530,
          easing: 'easeInOutQuad'
        },
        complete: () => {
          this.$anime.timeline().add({
            targets: '.animated-logo .second',
            translateX: {
              value: [-20, -20],
              duration: 0,
              delay: 0,
              easing: 'easeInQuad'
            },
            scale: {
              value: [0, 1],
              duration: 200,
              delay: 1500,
              easing: 'easeInQuad'
            },
            translateX: {
              value: [-20, -40],
              duration: 200,
              delay: 1950,
              easing: 'easeInQuad'
            }
          });

          this.$anime.timeline().add({
            targets: '.animated-logo .logo svg',
            translateX: {
              value: [0, -100],
              duration: 300,
              delay: 1650,
              easing: 'easeInQuad'
            },
          });

          this.$anime.timeline().add({
            targets: '.animated-logo .ball',
            translateX: {
              value: [-16, 0],
              duration: 600,
              delay: 1500,
              easing: 'easeInOutQuad'
            }
          });

          this.$anime.timeline().add({
            targets: '.animated-logo .all',
            translateX: {
              value: [-30, 0],
              duration: 600,
              delay: 1500,
              easing: 'easeInOutQuad'
            }
          });
        }
      });
    }
  },
  mounted() {
    this.animate();
    this.timer = setInterval(() => {
      this.animate()
    }, 6000)
  },

  beforeDestroy() {
    clearInterval(this.timer)
  }
}
</script>

<style lang="scss">
.logo-block {
  width: 130px;
  height: auto;
  margin-top: 0;
  margin-left: 5rem;
  @media (max-width: 850px) {
    width: 25px;
    margin-left: 0;
  }
}

.animated-logo {
  align-items: center;
  display: flex;
  justify-content: center;
  height: 100%;
  margin: 0;
  //overflow-x: hidden;
  position: absolute;
  width: 100%;
}
.animated-logo {
  .filter {
    left: 0;
    position: absolute;
    top: 0;
  }
  .all {}
  .ball {
    height: 60px;
    width: 60px;
  }
  .gooey {
    filter: url('#gooeyness');
    height: 60px;
    width: 60px;
  }
  .first {
    background-color: #00A672;
    border-radius: 50%;
    height: 60px;
    position: absolute;
    width: 60px;
  }
  .second {
    background-color: #00A672;
    border-radius: 50%;
    height: 26px;
    position: absolute;
    width: 22px;
    top: 15px;
    left: 75px;
    transform:  translateX(-35px);
  }
  .letter {
    fill: #FA787A;
    left: calc(50% - 20px);
    position: absolute;
    top: calc(50% - 38px);
    width: 38px;
  }
  .logo {
    fill: #232F5F;
    left: calc(50% + 46px);
    overflow: hidden;
    position: absolute;
    top: calc(50% - 48px);
    width: 0;
  }
  .logo svg {
    width: 100px;
  }
}
</style>
